mapper.insert:
  	INSERT INTO boardtable(
  			 BIDX
  			,TITLE
  			,CONTENT
  			,WDATE
  			,ID
  			,HIT
  			,LIKES
  			,DELYN
  			,CATEGORY
  			,IP
  			,UIDX
  			,BOARD_TYPE
  			 
  			,HIDE_ID
  			
  			)
		VALUES(
		     bidx_seq.nextval
		     ,#{title}
		     ,#{content}
		     ,sysdate
			 ,#{id}		     
		     ,0
		     ,0
		     ,'N'
		     ,#{category}
		     ,#{ip}
		     ,#{uidx}
		     ,#{board_type}
		     
		     ,#{hide_id}
		     
		     )

mapper.update:
UPDATE boardtable
   SET hit = hit+1
 WHERE bidx=#{bidx}
