mapper.select:
SELECT COUNT(*) 
  FROM liketable 
 WHERE bidx = #{bidx} AND id = #{id}

mapper.delete:
DELETE liketable 
 WHERE bidx = #{bidx} 
   AND id= #{id}

mapper.update:
UPDATE boardtable
   SET likes = likes + 1
 WHERE bidx = #{bidx}

UPDATE boardtable
   SET likes = likes - 1
 WHERE bidx = #{bidx}

mapper.select:
SELECT likes 
  FROM boardtable 
 WHERE bidx = #{bidx}
