mapper.select:
 		select pw
 			from usertable
 			where pw=#{pw}

mapper.update:
update reservationtable
   set rescancle = 'Y'
   where residx=#{residx}

update reservationtable
   set  resposition='예약취소'
   where residx=#{residx}

update reservationtable
   set  resposition='예약완료'
   where residx=#{residx}
