mapper.select:
SELECT COUNT(clidx) as cnt
FROM centerliketable
WHERE center=#{center} AND uidx=#{uidx}

mapper.update:
UPDATE centerliketable
SET like_check = like_check + 1 
WHERE uidx=#{uidx} AND center=#{center}

UPDATE centerliketable
SET like_check = 0
WHERE uidx=#{uidx} AND center=#{center}

mapper.select:
SELECT clidx, center, uidx, like_check 
FROM centerliketable
WHERE center=#{center} AND uidx=#{uidx}
