mapper.select:

	select * 
from (
	select rownum as rnum, a. *
	 from (
	 	select * 
	 	from  reviewtable
	 	 where uidx=${uidx}
	 	 order by rdate desc
 		)a 
		)
		WHERE RNUM BETWEEN (#{page}-1)*#{perPageNum}+1 AND #{page}*#{perPageNum}

select count(*) 
from reviewtable
where uidx=${uidx}

mapper.insert:

	insert into reviewtable(
		 ridx
		,rcontent
		,rdate
		,uidx
		,id
		
		
	)values(
		ridx_seq.nextval
		, #{rcontent}
		, sysdate
		, #{uidx}
		, #{id}
		
		)
