mapper.insert:

	insert into usertable(
		uidx
	   ,id
	   ,pw
	   ,mail
	   ,phone
	   ,usertype
	   ,edate
	  
	)values(
		  uidx_seq.nextval 
		  , #{id}
		  , #{pw}
		  , #{mail}
		  , #{phone}  
		  , #{usertype}
		  , sysdate
			)


