mapper.select:
select uidx
	 , id
	 , usertype
	 from usertable
	 where id = #{id}
	 and pw = #{pw}

select count(*)
 from usertable
 where id= #{id}

 
	select count(*)
		from usertable
		where mail = #{mail}

mapper.insert:

	insert into usertable(
		uidx
	   ,id
	   ,pw
	   ,mail
	   ,phone
	   ,usertype
	   ,edate
	  
	)values(
		  uidx_seq.nextval 
		  , #{id}
		  , #{pw}
		  , #{mail}
		  , #{phone}  
		  , #{usertype}
		  , sysdate
			)


	insert into usertable(
		uidx
	   ,name
	   ,id
	   ,pw
	   ,mail
	   ,phone
	   ,usertype
	   ,edate
	   
	   
	   
	)values(
		  uidx_seq.nextval 
		  , #{name}
		  , #{id}
		  , #{pw}
		  , #{mail}
		  , #{phone}  
		  , #{usertype}
		  , sysdate
			)

mapper.select:
select id,pw 
