mapper.select:
select count(*)
 from usertable
 where id= #{id}

 
	select count(*)
		from usertable
		where mail = #{mail}

 
	select count(*)
		from usertable
		where phone = #{phone}

select id 
 from usertable
 where mail = #{mail}

mapper.insert:

	insert into usertable(
		uidx
	   ,id
	   ,pw
	   ,mail
	   ,phone
	   ,usertype
	   ,edate
	  
	)values(
		  uidx_seq.nextval 
		  , #{id}
		  , #{pw}
		  , #{mail}
		  , #{phone}  
		  , #{usertype}
		  , sysdate
			)


	insert into usertable(
		uidx
	   ,name
	   ,id
	   ,pw
	   ,mail
	   ,phone
	   ,usertype
	   ,edate
	   
	   
	   
	)values(
		  uidx_seq.nextval 
		  , #{name}
		  , #{id}
		  , #{pw}
		  , #{mail}
		  , #{phone}  
		  , #{usertype}
		  , sysdate
			)

mapper.select:
		select count(id) as count
			from usertable
			where id = #{id} and pw = #{pw}

mapper.update:
update usertable
set mail =#{mail}
 ,phone =#{phone}
 
where uidx = #{uidx}

update usertable
   set delyn = 'Y'
   where pw =#{pw}
   and id =#{id}
   and uidx=#{uidx}

update usertable
	set pw = #{pw}
	where mail = #{mail}
