mapper.select:
	
        select *
       	  from (
       	  		select rownum as rnum, a.* 
       	  		  from ( 
       	  		  select bidx
					    ,title
					    ,content
					    ,wdate
					  
						from boardtable
						     where delyn='N' and board_type= '2'
						     order by bidx desc
						     )a
						     )	
        					WHERE rnum BETWEEN (#{page}-1)*#{perPageNum}+1 AND #{page}*#{perPageNum}
